CustomField.formula:
IF ( ISPICKVAL(Investor_Type__c , "Consultant"), "Consultant", 
IF ( ISPICKVAL(Investor_Type__c , "Advisory"), "Advisory",
     IF ( PFG_Total_Raised_Capital__c > 0 , "Investor", 
         IF ( AND( ISBLANK(PFG_Total_Raised_Capital__c), NOT(ISPICKVAL(Investor_Type__c , "Consultant"))), "Prospect", 
               IF ( AND(PFG_Total_Raised_Capital__c = 0), "Former Client", 
                "" 
         ) 
       ) 
   ) 
)
)
