WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/10.0/connection.js")}
{!REQUIRESCRIPT("/soap/ajax/10.0/apex.js")}
var url = parent.location.href;
var isTeamDefined = sforce.apex.execute("AddDefaultTeamButtonFunctionality","addDefaultTeam", {oppId:"{!Opportunity.Id}",ownerIds:"{!Opportunity.OwnerId}"});
if(isTeamDefined == "false") {
window.alert("The record owner does not have default opportunity team. You can define a default opportunity team in User Setup. ");
} 

else {
parent.location.href = url;
}
